<style scoped>
    .base-stick{
        width: clamp(16px,5%,30px);
        object-fit: contain;
        
        position: absolute;
        top: 20%;
        right: 40%;
        

    }

    @media (min-width: 640px) { 
        .base-stick{
            top: 0%;
            right: 40%;
        }
    }

    @media(min-width: 1024px){
        .base-stick{
            width: clamp(16px,10%,30px);
            top: 7.5%;
            right: 50%;
        }
    }

    @media(min-width: 1200px){
        .base-stick{
            top: 16%;
            right: 38%;
        }
    }


    .swinging-stick{
        width: clamp(200px,50vw,375px);
        object-fit: contain;
        
        position: absolute;
        top: 90%;
        right: -575%;

        animation: swaying 2s alternate infinite ease-in-out;
    }

    @media (min-width: 340px) {
        .swinging-stick{right: -500%;}
    }

    @media (min-width: 690px) {
        .swinging-stick{right: -575%;}
    }

    .right-bowl{
        width: clamp(100px, 20vw, 170px);
        object-fit: contain;
        position: absolute;
        top: 35%;
        left: -10%;

        animation: swaying-blow 2s alternate infinite ease-in-out;
    }

    .left-bowl{
        width: clamp(100px, 20vw, 170px);
        object-fit: contain;
        position: absolute;
        top: 35%;
        right: -10%;

        animation: swaying-blow 2s alternate infinite ease-in-out;
    }
    
    @keyframes swaying{
        from{transform: rotate(10deg);}
        to{transform: rotate(-10deg);}
    }

    @keyframes swaying-blow{
        from{ transform: rotate(-10deg) translateX(12%);}
        to{transform: rotate(10deg) translateX(-12%);}
    }

    .oslop{
        min-width: 150%;
        /* width: 250px; */
        height: 150%;
        object-fit: contain;
        position: absolute;
        bottom: 0;
        left: -52%;
        z-index: -10;
    }

    .constitution{
        width: 100%;
        object-fit: contain;
        position: absolute;
        bottom: 7%;
        left: 10%;
        z-index: -10;
    }
</style>